import React, { Component } from 'react'

export default class StudentInfo extends Component {
    constructor(props){
        super(props);

        this.state = {
            studentName: '',
            day: '', // Mon, Tue, Wed
            month: '', // Jan, Feb
            selectedDayofMonth: '', // 15th, 16th so on
            startTime: '',
            endTime: ''
        }
    }
    render() {
        return (
            <div>
                <div>
                    <h2>Your Next Lesson is in</h2>
                </div>
                <div>
                    Student: {this.state.studentName}
                </div>
                 <div>
                     <div>
                    {this.state.day},
                    {this.state.month} 
                    {this.state.selectedDayofMonth}
                     </div><div>
                         {this.state.startTime} - {this.state.endTime}
                     </div>
                </div>
            </div>
        )
    }
}
