import React, { Component } from 'react'

export default class StudentInfo extends Component {
    constructor(props){
        super(props);

        this.state = {
            studentName: 'Kartik',
            day: 'Wed', // Mon, Tue, Wed
            month: 'Aug', // Jan, Feb
            selectedDayofMonth: '30', // 15th, 16th so on
            startTime: '4:00pm',
            endTime: '5:00pm',
            lessonName: 'Maths',
            tutorName: 'Steve'
        }
    }
    render() {
        return (
            <div>
                <div>
                    <h3>Your Next Lesson is in</h3>
                </div>
                <div className="student-info-circle">
                <div>
                    Student: {this.state.studentName}
                </div>
                 <div>
                     <div>
                    {this.state.day},
                    {this.state.month} 
                    {this.state.selectedDayofMonth}
                     </div><div>
                         {this.state.startTime} - {this.state.endTime}
                     </div>
                </div>
                <div>
                    {this.state.lessonName} by {this.state.tutorName}
                </div>
            </div>
            </div>
        )
    }
}
