        super(props);

        this.state = {
            studentName: 'Kartik',
            day: 'Wed', // Mon, Tue, Wed
            month: 'August', // Jan, Feb
            selectedDayofMonth: '30', // 15th, 16th so on
            startTime: '4:00pm',
            endTime: '5:00pm',
            lessonName: 'Maths',
            tutorName: 'Steve'
        }
    }
