        {
        var Difference_In_Time = date1.getTime() - currentdate.getTime();
        }

        console.log(currentdate);
    }
    render() {
        return (
            <div className="oval-container">
                <div>
                    <h3>Your Next Lesson is in</h3>
                </div>
                <div className="student-info-circle">
                <div className="details">
                    Student <br/>{this.state.studentName}
                </div>
                 <div className="details">
                     <div>
                    {this.state.day},
                    {this.state.month} 
                    {this.state.selectedDayofMonth}
                     </div><br/><div>
                         {this.state.startTime} - {this.state.endTime}
                     </div>
                </div>
                <div className="details">
                    {this.state.lessonName} by {this.state.tutorName}
                </div>
            </div>
            </div>
        )
    }
}
